import React from 'react';
import './waterpage.styles.scss';
import { Link } from 'react-router-dom';
import firebase from '../../firebase';
//import arrowleft from '../../images/arrow-left.svg';
//import arrowleft2 from '../../images/arrow-left2.svg';

class Waterpage extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            aantal: 0,
        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.stopzetten = this.stopzetten.bind(this);
    }

    componentDidMount(){
        var status = this.props.statusparam;
        console.log(this.props.statusparam);
        this.setState({status: status});
    }

    handleChange = (e) => {
        if(e.target.name ==="aantal"){
            console.log(e.target.value);
            this.setState({aantal: e.target.value});
        }

        else if(e.target.name ==="eenheid"){
            if(e.target.value ==="minuten"){
                this.setState({eenheid: e.target.value});
                document.getElementById("aantal").style.visibility="visible"; 
            } else if(e.target.value ==="milimeter"){
                this.setState({eenheid: e.target.value});
                document.getElementById("aantal").style.visibility="visible"; 
            } else if (e.target.value ==="maximum"){
                this.setState({eenheid: e.target.value});
                document.getElementById("aantal").style.visibility="hidden"; 
            }
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();
        const db = firebase.firestore();

        var currentDate = new Date();

            db.collection("watertoevoer").add(
                {
                aan: true,
                datum: currentDate,
                manier: "manueel",
                minuten: 80,
                milimeter: 40,
            });
    }

    stopzetten = (e) => {
        e.preventDefault();
        const db = firebase.firestore();

        var currentDate = new Date();
        db.collection("watertoevoer").add(
            {
            aan: false,
            datum: currentDate,
            manier: "manueel",
            minuten: 0,
            milimeter: 0,
        });
    }

    render(){
        const {status} = this.state;
        console.log(this.state);

        return(
            <div className='waterpage'>
                <div className="pagetitle">WATERNIVEAU<br></br>{status}</div>
                <div className="back"><Link to='/'>Ga terug</Link></div>
                    <form onSubmit={this.handleSubmit}>
                            <input type="submit" className="manueel" value="MANUEEL BIJVULLEN"/>
                    </form>

                    <form onSubmit={this.stopzetten}>
                            <input type="submit" className="stopzetten" value="STOPZETTEN BIJVULLING"/>
                    </form>
                <div className="geschiedenis"><Link to='/geschiedenis'>GESCHIEDENIS EN TREND</Link></div>


            </div> 
        );
    }
}

export default Waterpage;
