import React from 'react';
import './temperatuur.styles.scss';
import firebase from '../../firebase';

class Temperatuur extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            temperatures: [],
        }
    }
    /*componentDidMount() {
        firebase
        .firestore()
