//import React, { useState } from 'react';
import React from 'react';
import './waterniveau.styles.scss';
import firebase from '../../firebase';

class Waterniveau extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            status: "okay", //okay, filling or something else
        }
    }

    componentDidMount() {
        var items = [];
        firebase
        .firestore()
        .collection("watertoevoer").orderBy("datum", "desc").limit(1)
        .get()
        .then((querySnapshot) => {  //Notice the arrow funtion which bind `this` automatically.
            querySnapshot.forEach(function(doc) {
                items.push(doc.data());
            });
            this.setState({ items: items });   //set data in state here
        });
    }

    render(){
        //make de error pinking
        const {status} = this.state;
        console.log(this.state);

        return(
            <div>
                    {status === "okay" ? 
                        <div className='waterniveaugreen'>
                            <div className="title">WATERNIVEAU</div>
                            <div className="status">STATUS: in orde</div>
                        </div>
                    : status === "filling" ? 
                        <div className='waterniveaured'>   
                            <div className="title">WATERNIVEAU</div>
                            <div className="statuswhite">STATUS: aan het bijvullen </div>
                        </div>
                    : 
                        <div className='waterniveaugrey'>
                            <div className="title">WATERNIVEAU</div>
                            <div className="status">ERROR: Controleer de aansluiting </div>

                        </div> 
                    }

            </div>


        );
    }
}

export default Waterniveau;
